Centroids(clusters); // T√≠nh to√°n l·∫°i v·ªã tr√≠ tr·ªçng t√¢m c√°c c·ª•m } while (changed); return clusters; } private static List<Cluster> initializeClusters(List<Point> points) { List<Cluster> clusters = new ArrayList<>(); for (int i = 0; i < K; i++) { Point centroid = points.get(i); Cluster cluster = new Cluster(centroid); clusters.add(cluster); } return clusters; } private static void assignPointsToClusters(List<Point> points, List<Cluster> clusters) { for (Cluster cluster : clusters) { cluster.getPoints().clear(); } for (Point point : points) { Cluster closestCluster = clusters.get(0); double shortestDistance = Double.MAX_VALUE; for (Cluster cluster : clusters) { double distance = calculateDistance(point, cluster.getCentroid()); if (distance < shortestDistance) { shortestDistance = distance; closestCluster = cluster; } } closestCluster.getPoints().add(point); } } private static boolean calculateNewCentroids(List<Cluster> clusters) { boolean changed = false; for (Cluster cluster : clusters) { Point oldCentroid = cluster.getCentroid(); Point newCentroid = calculateCentroid(cluster.getPoints()); if (!newCentroid.equals(oldCentroid)) { cluster.setCentroid(newCentroid); changed = true; } } return changed; } private static Point calculateCentroid(List<Point> points) { double sumX = 0, sumY = 0; for (Point point : points) { sumX += point.getX(); sumY += point.getY(); } double centroidX = sumX / points.size(); double centroidY = sumY / points.size(); return new Point(centroidX, centroidY); } private static double calculateDistance(Point p1, Point p2) { double dx = p1.getX() - p2.getX(); double dy = p1.getY() - p2.getY(); return Math.sqrt(dx * dx + dy * dy); } private static void printClusters(List<Cluster> clusters) { for (int i = 0; i < clusters.size(); i++) { System.out.println(\\"Cluster \\" + (i + 1) + \\":\\"); for (Point point : clusters.get(i).getPoints()) { System.out.println(\\"(\\" + point.getX() + \\", \\" + point.getY() + \\")\\"); } System.out.println(); } } private static class Point { private double x; private double y; public Point(double x, double y) { this.x = x; this.y = y; } public double getX() { return x; } public double getY() { return y; } @Override public boolean equals(Object obj) { if (this == obj) { return true; } if (obj == null || getClass() != obj.getClass()) { return false; } Point other = (Point) obj; return x == other.x && y == other.y; } } private static class Cluster { private Point centroid; private List<Point> points; public Cluster(Point centroid) { this.centroid = centroid; this.points = new ArrayList<>(); } public Point getCentroid() { return centroid; } public void setCentroid(Point centroid) { this.centroid = centroid; } public List<Point> getPoints() { return points; } } } \`\`\` V·ªõi d·ªØ li·ªáu ƒë·∫ßu v√†o nh∆∞ sau: \`\`\` 1,2 3,5 2,4 7,3 9,6 \`\`\` ƒê·∫ßu ra s·∫Ω l√†: \`\`\` Cluster 1: (1.0, 2.0) (3.0, 5.0) (2.0, 4.0) Cluster 2: (7.0, 3.0) (9.0, 6.0) \`\`\`"},{"prompt":"23. –†–∞—Ü–∏–æ–Ω–∞–ª—å–Ω—ã–π –£–Ω–∏—Ñ–∏—Ü–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –ü—Ä–æ—Ü–µ—Å—Å. –°—Ç–∞—Ç–∏—á–µ—Å–∫–æ–µ —Å–æ–¥–µ—Ä–∂–∞–Ω–∏–µ –ø—Ä–æ—Ü–µ—Å—Å–∞: –≤–∏–¥—ã –¥–µ—è—Ç–µ–ª—å–Ω–æ—Å—Ç–∏ (—Ç–µ—Ö–Ω–æ–ª–æ–≥–∏—á–µ—Å–∫–∏–µ –æ–ø–µ—Ä–∞—Ü–∏–∏), —Ä–∞–±–æ—á–∏–µ –ø—Ä–æ–¥—É–∫—Ç—ã, –∏—Å–ø–æ–ª–Ω–∏—Ç–µ–ª–∏ –∏ –¥–∏—Å—Ü–∏–ø–ª–∏–Ω—ã (—Ç–µ—Ö–Ω–æ–ª–æ–≥–∏—á–µ—Å–∫–∏–µ –ø—Ä–æ—Ü–µ—Å—Å—ã) . –ø–æ–¥—Ä–æ–±–Ω–æ","completion":"–†–∞—Ü–∏–æ–Ω–∞–ª—å–Ω—ã–π –£–Ω–∏—Ñ–∏—Ü–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –ü—Ä–æ—Ü–µ—Å—Å (Rational Unified Process, RUP) - —ç—Ç–æ –≥–∏–±–∫–∞—è –º–µ—Ç–æ–¥–æ–ª–æ–≥–∏—è —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–∏ –ø—Ä–æ–≥—Ä–∞–º–º–Ω–æ–≥–æ –æ–±–µ—Å–ø–µ—á–µ–Ω–∏—è, –∫–æ—Ç–æ—Ä–∞—è –æ–±–µ—Å–ø–µ—á–∏–≤–∞–µ—Ç —Å–∏—Å—Ç–µ–º–Ω—ã–π –ø–æ–¥—Ö–æ–¥ –∫ —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–µ, —É–ø—Ä–∞–≤–ª–µ–Ω–∏—é –∏ –ø–æ–¥–¥–µ—Ä–∂–∫–µ –ø—Ä–æ–≥—Ä–∞–º–º–Ω—ã—Ö –ø—Ä–æ–¥—É–∫—Ç–æ–≤. –°—Ç–∞—Ç–∏—á–µ—Å–∫–æ–µ —Å–æ–¥–µ—Ä–∂–∞–Ω–∏–µ –ø—Ä–æ—Ü–µ—Å—Å–∞ RUP –≤–∫–ª—é—á–∞–µ—Ç –≤ —Å–µ–±—è —Å–ª–µ–¥—É—é—â–∏–µ —ç–ª–µ–º–µ–Ω—Ç—ã: 1. –í–∏–¥—ã –¥–µ—è—Ç–µ–ª—å–Ω–æ—Å—Ç–∏ (—Ç–µ—Ö–Ω–æ–ª–æ–≥–∏—á–µ—Å–∫–∏–µ –æ–ø–µ—Ä–∞—Ü–∏–∏): - –ò—Ç–µ—Ä–∞—Ü–∏—è: —Ä